import React from "react";
import  {Avatar, Card, CardActions, CardContent, CardHeader, CardMedia, IconButton, Typography} from '@material-ui/core';
import MoreVertIcon from "@material-ui/icons/MoreVert";
import FavoriteIcon from "@material-ui/icons/Favorite";
import moment from 'moment';


export default function Post({post}){
    return <Card>
        <CardHeader 
        avatar = {<Avatar>A</Avatar>}
        title={post.author}
        subheader = {moment(post.updatedAt).format('HH:MM DD MM,YYYY ')}
        action={
            <IconButton>
               <MoreVertIcon/> 
            </IconButton>
        }
        />

        <CardMedia image = {post.attachment} title="Title"/>
        <CardContent>
            <Typography variant='h5' color='textPrimary'>Titre</Typography>
                {post.title}
            <Typography variant='boby2' component="p" color='textSecondary'>Contenu</Typography>
                {post.content}
        </CardContent>

        <CardActions>
            <IconButton>
                <FavoriteIcon/>
                <Typography component="span" color="textSecondary">
                    {post.likeCount}
                </Typography>
            </IconButton>
        </CardActions>
    </Card>
}
